    scanf("%d", &n);
    for (int i = 0; i < n; i++) scanf("%d", &a[i]);
    for (int i = 100; i <= 999; i++)
    {
        for (int j = 10; j <= 99; j++)
        {
            int now1 = i * (j % 10), now2 = i * (j / 10), now3 = i * j;
            if (find(i, 3) && find(j, 2) && find(now1, 3) && find(now2, 3) && find(now3, 4)) ans++;
        }
    }
    printf("%d\n", ans);
    return 0;
}
