    putchar(x % 10 + '0');
}

struct edge
{
    int y, w;
};

struct node
{
    int dis;
    bool find;
} d[N];

struct nodep
{
    int dis;
    int pos;
    bool operator <(const nodep &x) const
    {
        return x.dis < dis;
    }
};

vector<edge> mp[N];
priority_queue<nodep> q;
int n, m;

