}
*/

void dij(int first)
{
    for (int i = 1; i <= n; i++) d[i].dis = INF;
    d[first].dis = 0;
    int y, w;
    nodep temp;
    temp.dis=0,
    temp.pos=first;
    q.push(temp);
    while (!q.empty())
    {
        temp = q.top();
        q.pop();
        int x = temp.pos;
        if (d[x].find) continue;
        d[x].find = true;
        for (int i = 0; i < (int)mp[x].size(); i++)
        {
            y = mp[x][i].y, w = mp[x][i].w;
            if (d[y].dis > d[x].dis + w)
            {
                d[y].dis = d[x].dis + w;
                if (!d[y].find)
                {
                    temp.dis=d[y].dis;
                    temp.pos=y;
                    q.push(temp);
                }
            }
        }
    }
}

int main()
{
    int first, x, y, w;
    edge qwq;
    in(n), in(m), in(first);
    for (int i = 0; i < m; i++)
    {
        in(x), in(y), in(w);
        qwq.y = y, qwq.w = w;
        mp[x].push_back(qwq);
    }
    dij(first);
    for (int i = 1; i <= n; i++)
    {
        out(d[i].dis);
        printf(" ");
    }
    cout << endl;
    return 0;
}
