    for (int i = 2; i <= n; i++)
    {
        if (!vis[i]) prime[++ptot] = i;
        for (int j = 1; j <= ptot && i * prime[j] <= n; j++)
        {
            vis[i * prime[j]] = true;
            if (i % prime[j] == 0) break;
        }
    }
}

bool check(int x)
{
    int temp[10], tot = 0;
    while (x)
    {
        temp[++tot] = x % 10;
        x /= 10;
    }
    for (int i = 1; i <= tot; i++)
        if (temp[i] != temp[tot - i + 1]) return false;
    return true;
}

